import React from "react";
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { Container } from "@mui/system";
import SearchBar from "./SearchBar";
import { Link } from 'react-router-dom';
import { Paper } from "@mui/material";

const windowInnerWidth = document.documentElement.clientWidth;
const minWidth = 1200;

function Header() {
  let pos="sticky";
  if (windowInnerWidth < minWidth) {
    pos="static";
  }
  return(
    <>
    <Container fixed>
    <Paper>
    <AppBar position={pos}>
      <Container fixed>
        <Toolbar>
          <Typography
            variant="h6"
            noWrap
            component={Link}
            to="/"
            sx={[
              {
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
              },
              (theme) => ({
                '&:hover': {
                  color: '#66bb6a',
                }
              })
          ]
        }
          >
            RAZBOR
          </Typography>
          {windowInnerWidth >= minWidth &&
          <SearchBar />
          }
          <Box sx={{ flexGrow: 1 }} />
          <Box mr={3}>
            <Button component={Link} to="/login" color = "inherit" variant="outlined">Вход</Button>
          </Box>
          <Button component={Link} to="/register" color = "secondary" variant="contained">Регистрация</Button>
        </Toolbar>
      </Container>
    </AppBar>
    </Paper>
    </Container>
    {windowInnerWidth < minWidth &&
          <AppBar position="sticky">
            <Box display="flex"
                justifyContent="center"
            >
            <Toolbar>
              <SearchBar />
            </Toolbar>
            </Box>
          </AppBar>
          }
    </>
  );
}

export default Header;
