import React from "react";
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { Container } from "@mui/system";
import SearchBar from "./SearchBar";
import { Link, Navigate } from 'react-router-dom';
import LoginForm from "./LoginForm";


const windowInnerWidth = document.documentElement.clientWidth;
const minWidth = 1200;


function Header() {
  let pos="sticky";
  if (windowInnerWidth < minWidth) {
    pos="static";
  }

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const toReg = () => {
    const thisUrl = window.location.href;
    console.log(thisUrl);
    Navigate("/register", { state: {thisUrl}});
  };

  return(
    <>
    <AppBar position={pos}>
      <Container fixed>
        <Toolbar>
          <Typography
            variant="h6"
            noWrap
            component={Link}
            to="/"
            sx={[
              {
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
              },
              (theme) => ({
                '&:hover': {
                  color: '#66bb6a',
                }
              })
          ]
        }
          >
            RAZBOR
          </Typography>
          {windowInnerWidth >= minWidth &&
          <SearchBar />
          }
          <Box sx={{ flexGrow: 1 }} />
          <Box mr={3}>
            <Button onClick={handleClickOpen} color = "inherit" variant="outlined">Вход</Button>

            <LoginForm 
              open={open}
              onClose={handleClose}
            />

          </Box>
          <Button component={Link} to="/register" color = "secondary" variant="contained">Регистрация</Button>
        </Toolbar>
      </Container>
    </AppBar>
    {windowInnerWidth < minWidth &&
          <AppBar position="sticky">
            <Box display="flex"
                justifyContent="center"
            >
            <Toolbar>
              <SearchBar />
            </Toolbar>
            </Box>
          </AppBar>
          }
    </>
  );
}

export default Header;
