import React from "react";
import { observer } from 'mobx-react';
//import store from '../../stores/LoginStore';
import { Paper, Button, Dialog, DialogActions,DialogContent, DialogContentText, DialogTitle, Grid, TextField, Typography, Divider } from "@mui/material";
import { Box , Container} from "@mui/system";
import {isMobile} from 'react-device-detect';
import { useNavigate } from 'react-router-dom';

import login from '../../../stores/LoginStore';
import ROLES from '../../../stores/Engine';

const logged = login.isLogged();
const isadmin = login.getRole() === ROLES.ADMIN;

function NewCar(props){
    const navigate = useNavigate();
    if(!logged || !isadmin)
        navigate('/');

    let widthTextField = '45ch';
    if(isMobile) widthTextField = '95%';

    // const SelImage = () => {
    //     navigate('/admin/newcar');
    // };

    const [carPhoto, setImage] = React.useState(null)

    const SelImage = event => {
        if (event.target.files && event.target.files[0]) {
            setImage(URL.createObjectURL(event.target.files[0]));
          }
      };

    return(
        <Box>
            <Container fixed>
                <Paper sx={{mt: 2}}>
                    <Box display="flex"
                    justifyContent="center"
                    component="form"
                    sx={{p: 2,
                        '& .MuiTextField-root': { m: 1, minWidth: widthTextField, maxWidth: widthTextField }}}
                    noValidate
                    autoComplete="off"
                    >
                        <Grid container alignItems='center' justifyContent='center' maxWidth='md' spacing={6}>
                            <Grid item>   
                                <Typography variant="h4" align="center">Добавление машины</Typography>
                            </Grid>
                            <Grid item>
                                <TextField
                                    autoFocus
                                    margin="dense"
                                    id="brand"
                                    label="Марка(производитель)"
                                    fullWidth
                                    variant="outlined"/>
                                <TextField
                                    autoFocus
                                    margin="dense"
                                    id="model"
                                    label="Модель"
                                    fullWidth
                                    variant="outlined"/>
                            </Grid>
                            <Grid item>
                            <input
                                accept="image/*"
                                //className={classes.input}
                                style={{ display: 'none' }}
                                id="raised-button-file"
                                multiple
                                type="file"
                                onChange={SelImage}
                                />
                                <label htmlFor="raised-button-file">
                                <Button variant="raised" component="span" //className={classes.button}>
                                >
                                    Выбрать фото автомобиля
                                </Button>
                                </label> 
                            </Grid>
                            {carPhoto!=null && <Grid item>
                                <img alt="Выбранное фото" src={carPhoto}/>
                            </Grid>}
                        </Grid>
                    </Box>
                </Paper>
            </Container>
        </Box> 
    )
}

export default observer(NewCar);
