import React from "react";
import { Button, Dialog, DialogActions,DialogContent, DialogContentText, DialogTitle, Grid, TextField, Typography } from "@mui/material";
import { Box } from "@mui/system";

function LoginForm(props){
    const { onClose, open, toReg } = props;

    const handleClose = () => {
        onClose();
    };

    const handleReg = () => {
        toReg();
        onClose();
    };
    /*const handleListItemClick = (value) => {
        onClose(value);
    };*/
    return(
        <Dialog open={open} onClose={handleClose} aria-labelledby="Login">
            <DialogTitle id="Login">Вход</DialogTitle>
            <DialogContent>
                <TextField
                    autoFocus
                    margin="dense"
                    id="name"
                    label="Имя"
                    fullWidth
                    variant="outlined"/>
                <TextField
                    margin="dense"
                    id="password"
                    label="Пароль"
                    fullWidth
                    variant="outlined"
                    type="password"/>
            </DialogContent>
            <DialogActions>
                <Button onClick={handleClose}>Отмена</Button>
                <Button onClick={handleClose}>Войти</Button>
            </DialogActions>
            <hr />
            <Box 
                display="flex"
                justifyContent="center"
                alignItems="center" >
                    <Typography>Нет аккаунта?</Typography>
                    <Button onClick={handleReg}>Регистрация</Button>
            </Box>

        </Dialog>
    )
}

export default LoginForm;
