import React from "react";
import { observer } from 'mobx-react';
import login from '../../stores/LoginStore';
import ROLES from '../../stores/Engine';
import { useNavigate } from 'react-router-dom';

import LoginForm from "./LoginForm";

import { Container, Box } from "@mui/system";
import { Paper, Button, Typography } from "@mui/material";

import {isMobile} from 'react-device-detect';

const logged = login.isLogged();
const isadmin = login.getRole() === ROLES.ADMIN;

function Panel(){
    const navigate = useNavigate();

    const newCar = () => {
        if (!login.isLogged())
            navigate('/admin/newcar');
    };

    let widthTextField = '45ch';
    if(isMobile) {
        widthTextField = '95%';
    }

    return(

        <>
            <Box
                display="flex"
                justifyContent="center"
                component="form"
                sx={{p: 2,
                    '& .MuiTextField-root': { m: 1, minWidth: widthTextField, maxWidth: widthTextField }}}
                noValidate
                autoComplete="off"
                ></Box>
            <Typography variant="h3" align="center">Панель администратора</Typography>
            <Button onClick={newCar} color = "inherit" variant="outlined">Добавить модель машины</Button>
        </>
    )
}

function AdminPanel(){
    const navigate = useNavigate();

    const handleClose = () => {
        if (!login.isLogged())
            navigate('/');
      };

    if(logged && !isadmin)
        navigate('/');
    return(
        <Box>
        <Container fixed>
            <Paper sx={{mt: 2}}>
                <LoginForm 
                    open={!logged}
                    onClose={handleClose}
                    toReg={null}
                />
                {isadmin&& <Panel /> }
            </Paper>
        </Container>
        </Box> 
    )
}

export default observer(AdminPanel);
