import { computed, flow, makeObservable, observable } from "mobx";
import Api from '../utils/Api';
import serv from '../../config';

class AuthStore {
    token = null;

    constructor() {
        makeObservable(this, {
            token: observable,
            register: flow,
            user: computed,
            isLoggedIn: computed
        });
    }

    register(nickname, password, phone, role, name, surname, address, darktheme) {
        const {token} = new Api().post(
            serv+'/api/auth/register',
            {nickname, password, phone, role, name, surname, address, darktheme}
        );

        if(token) {
            this.token = token;
        }
    }

    get user() {
        if (!this.token) {
          return null;
        }

        const [, encodedPayload,] = this.token.split('.');

        if (!encodedPayload) {
          return null;
        }

        try {
          return JSON.parse(window.atob(encodedPayload)).sub;
        } catch(e) {
          return null
        }
      }

    get isLoggedIn() {
        return Boolean(this.user);
      }
}

export default new AuthStore();
