import React from "react";
import { Container, Box } from "@mui/system";
import { Button, Grid, Paper, TextField, Typography, Divider, ButtonGroup, ToggleButtonGroup, ToggleButton } from "@mui/material";
import { useNavigate, useLocation } from "react-router-dom";
import {isMobile} from 'react-device-detect';


function RegNaturalPerson(){
    return(
        <>
            <TextField 
                required
                id = "name"
                label = "Имя"
                helperText="Другие пользователи будут знать, как к вам обращаться"
            />
            <TextField 
                id = "surname"
                label = "Фамилия(или отчество)"
            />
        </>
    )
}

function RegLegalPerson(){
    return(
        <>
            <TextField 
                required
                id = "name"
                label = "Название вашей компании"
                helperText="Другие пользователи будут знать, как к вам обращаться"
            />
            <TextField 
                required
                id = "address"
                label = "Адрес компании"
                helperText="Клиенты будут знать, где вас найти"
            />
        </>
    )
}


function RegisterForm(){
    //const { urlBefore } = props;
    const [alignment, setAlignment] = React.useState('web');
    let widthTextField = '45ch';
    if(isMobile) {
        widthTextField = '95%';
    }
    const location = useLocation();
    const urlBefore = location.state;
    const afterReg = () => {
        document.location.assign(urlBefore);
    }
    /*let but1 = "contained";
    let but2 = "primary";

    const natural = () => {
        but1 = "contained";
        but2 = "primary";
        console.log('1234');
    }
    const legal = () => {
        but1 = "contained";
        but2 = "primary";
        console.log('sdfg');
    }*/


    const [natural, setPers] = React.useState("natural");

    const changePerson = (event, newValue) => {
        setPers(newValue);
    };

    const [matchPass, setMatchPass] = useState(false);
    let checkPassText = "Чтобы точно запомнить";
    const checkPasswords = (event) => {
        if (event.tatget.value != document.getElementById('password').value){
            checkPassText = "Пароли не совпадают";
        }
    };

    const registration = () => {

    };

    return(
        //<Button onClick={afterReg}>ananas</Button>
        <Box>
        <Container fixed>
            <Paper sx={{mt: 2}}>
                <Box
                display="flex"
                justifyContent="center"
                component="form"
                sx={{p: 2,
                    '& .MuiTextField-root': { m: 1, minWidth: widthTextField, maxWidth: widthTextField }}}
                noValidate
                autoComplete="off"
                >
                <Grid container alignItems='center' justifyContent='center' maxWidth='md' rowSpacing={2}>
                    <Grid item>   
                    <Typography align="center" variant="h4">
                        Регистрация
                    </Typography>
                    <Typography align="center" variant="h6">Заполните поля(отмеченные под * обязательные к заполнению):</Typography>
                    </Grid>
                    <Grid item>   
                        <TextField 
                        required
                        id = "nickname"
                        label = "Имя аккаунта"
                        helperText="Будет использоваться при входе в систему"
                        />
                        <TextField 
                        required
                        id = "number"
                        label = "Номер телефона"
                        helperText="По номеру телефона пользователи смогут связаться с вами"
                        />
                    </Grid>
                    <Grid item>   
                        <TextField 
                        required
                        id = "password"
                        label = "Пароль"
                        helperText="Будет использоваться при входе в систему"
                        type="password"
                        />
                        <TextField 
                        required
                        id = "checkpass"
                        label = "Повторите пароль"
                        helperText={checkPassText}
                        type="password"
                        onChange={checkPasswords}   
                        />
                    </Grid>
                    <Grid item> 
                        <ToggleButtonGroup
                            value={natural}
                            exclusive
                            onChange={changePerson}
                        >
                            <ToggleButton value="natural">Физическое лицо</ToggleButton>
                            <ToggleButton value="legal">Юридическое лицо</ToggleButton>
                        </ToggleButtonGroup>
                    </Grid>
                    <Grid item> 
                        {natural === "natural" ? (
                            <RegNaturalPerson />
                        ) : (
                            <RegLegalPerson />
                        )}
                    </Grid>
                    <Grid item>
                    <Button >
                        Зарегистрироваться
                    </Button>
                    </Grid>
                </Grid> 



                </Box>
            </Paper>
        </Container>
        </Box>
    )
}

export default RegisterForm;
